import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/ViewCart.css';

const ViewCart = () => {
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [orderNotes, setOrderNotes] = useState('');
    const [couponCode, setCouponCode] = useState('');
    const [country, setCountry] = useState('USA');
    const [postalCode, setPostalCode] = useState('');
    
    // Constants
    const userId = 198;
    const shopId = 512;
    const imagePrefix = "https://s3.ap-south-1.amazonaws.com/business.strackit.com/";
    const freeShippingThreshold = 100; // Example threshold for free shipping in USD
    
    useEffect(() => {
        // Fetch cart data based on user ID and shop ID
        fetchCartData();
    }, []);
    
    const fetchCartData = async () => {
        try {
            setLoading(true);
            // Normally you would fetch from an API, but for this example, we'll use mock data
            // In a real implementation, replace this with your actual API call
            setTimeout(() => {
                const mockCartData = [
                    {
                        id: 1,
                        name: "Premium Cotton T-Shirt",
                        price: 29.99,
                        quantity: 2,
                        title: "Default Title",
                        image: "path/to/product1.jpg",
                    },
                    {
                        id: 2,
                        name: "Slim Fit Jeans",
                        price: 59.99,
                        quantity: 1,
                        title: "Default Title",
                        image: "path/to/product2.jpg",
                    }
                ];
                
                setCartItems(mockCartData);
                setLoading(false);
            }, 500);
        } catch (error) {
            console.error("Error fetching cart data:", error);
            setLoading(false);
        }
    };
    
    const updateQuantity = (id, change) => {
        setCartItems(prevItems => 
            prevItems.map(item => 
                item.id === id ? 
                { ...item, quantity: Math.max(1, item.quantity + change) } : 
                item
            )
        );
    };
    
    const removeItem = (id) => {
        setCartItems(prevItems => prevItems.filter(item => item.id !== id));
    };
    
    // Calculate cart totals
    const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    const tax = subtotal * 0.07; // Example tax rate of 7%
    const shipping = subtotal >= freeShippingThreshold ? 0 : 5.99;
    const total = subtotal + tax + shipping;
    
    // Calculate progress towards free shipping
    const shippingProgress = subtotal >= freeShippingThreshold ? 100 : (subtotal / freeShippingThreshold) * 100;
    const remainingForFreeShipping = freeShippingThreshold - subtotal > 0 ? freeShippingThreshold - subtotal : 0;
    
    if (loading) {
        return <div className="view-cart-container loading">Loading cart...</div>;
    }
    
    return (
        <div className="view-cart-container">
            <div className="cart-layout">
                {/* Left section - Cart items */}
                <div className="cart-items-section">
                    <h1 className="cart-title">Your Cart</h1>
                    
                    {cartItems.length === 0 ? (
                        <div className="empty-cart-message">
                            <p>Your cart is currently empty.</p>
                            <Link to="/" className="continue-shopping-link">CONTINUE SHOPPING</Link>
                        </div>
                    ) : (
                        <>
                            <div className="cart-table">
                                <div className="cart-header">
                                    <div className="header-product">PRODUCT</div>
                                    <div className="header-quantity">QUANTITY</div>
                                    <div className="header-total">TOTAL</div>
                                </div>
                                
                                {cartItems.map(item => (
                                    <div className="cart-item-row" key={item.id}>
                                        <div className="item-product">
                                            <div className="item-image">
                                                <img 
                                                    src={item.image.startsWith('path') ? 
                                                        'https://via.placeholder.com/80' : // Placeholder for demo
                                                        `${imagePrefix}${item.image}`} 
                                                    alt={item.name} 
                                                />
                                            </div>
                                            <div className="item-details">
                                                <h3 className="item-name">{item.name}</h3>
                                                <p className="item-price">${item.price.toFixed(2)} USD</p>
                                                <p className="item-title">Title: <span className="bold">{item.title}</span></p>
                                                <button 
                                                    className="delete-item" 
                                                    onClick={() => removeItem(item.id)}
                                                >
                                                    DELETE
                                                </button>
                                            </div>
                                        </div>
                                        <div className="item-quantity">
                                            <div className="quantity-stepper">
                                                <button 
                                                    className="quantity-btn minus" 
                                                    onClick={() => updateQuantity(item.id, -1)}
                                                    disabled={item.quantity <= 1}
                                                >
                                                    -
                                                </button>
                                                <span className="quantity-value">{item.quantity}</span>
                                                <button 
                                                    className="quantity-btn plus" 
                                                    onClick={() => updateQuantity(item.id, 1)}
                                                >
                                                    +
                                                </button>
                                            </div>
                                        </div>
                                        <div className="item-total">
                                            ${(item.price * item.quantity).toFixed(2)}
                                        </div>
                                    </div>
                                ))}
                            </div>
                            
                            <div className="cart-footer">
                                <Link to="/" className="continue-shopping-link">CONTINUE SHOPPING</Link>
                            </div>
                        </>
                    )}
                </div>
                
                {/* Right section - Order summary */}
                <div className="order-summary-section">
                    <div className="order-summary-container">
                        {remainingForFreeShipping > 0 && (
                            <div className="shipping-progress">
                                <p>
                                    Buy ${remainingForFreeShipping.toFixed(2)} USD more to enjoy FREE shipping
                                </p>
                                <div className="progress-bar-container">
                                    <div 
                                        className="progress-bar" 
                                        style={{ width: `${shippingProgress}%` }}
                                    ></div>
                                    <span className="progress-star-icon">â˜…</span>
                                </div>
                            </div>
                        )}
                        
                        <div className="order-notes">
                            <h3>Order notes</h3>
                            <textarea 
                                placeholder="Special instructions for your order"
                                value={orderNotes}
                                onChange={(e) => setOrderNotes(e.target.value)}
                            />
                        </div>
                        
                        <div className="coupon-section">
                            <h3>Coupon code</h3>
                            <div className="coupon-input-container">
                                <input 
                                    type="text" 
                                    placeholder="Enter coupon"
                                    value={couponCode}
                                    onChange={(e) => setCouponCode(e.target.value)}
                                />
                                <button className="apply-coupon">APPLY</button>
                            </div>
                        </div>
                        
                        <div className="shipping-section">
                            <h3>Shipping</h3>
                            <div className="shipping-inputs">
                                <select 
                                    value={country}
                                    onChange={(e) => setCountry(e.target.value)}
                                >
                                    <option value="USA">United States</option>
                                    <option value="CAN">Canada</option>
                                    <option value="GBR">United Kingdom</option>
                                    <option value="AUS">Australia</option>
                                </select>
                                
                                <input 
                                    type="text" 
                                    placeholder="Postal/ZIP code"
                                    value={postalCode}
                                    onChange={(e) => setPostalCode(e.target.value)}
                                />
                            </div>
                        </div>
                        
                        <div className="order-totals">
                            <div className="total-row">
                                <span>Subtotal</span>
                                <span>${subtotal.toFixed(2)}</span>
                            </div>
                            <div className="total-row">
                                <span>Tax</span>
                                <span>${tax.toFixed(2)}</span>
                            </div>
                            <div className="total-row">
                                <span>Shipping</span>
                                <span>{shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}</span>
                            </div>
                            <div className="total-row grand-total">
                                <span>TOTAL</span>
                                <span>${total.toFixed(2)}</span>
                            </div>
                            <p className="taxes-info">Taxes and shipping calculated at checkout</p>
                        </div>
                        
                        <button className="checkout-button">CHECK OUT</button>
                        
                        <div className="payment-methods">
                            <img src="/payment-methods.png" alt="Payment methods" />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ViewCart;
