import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiMinus, FiPlus, FiChevronDown } from 'react-icons/fi';
import { fetchCart, removeFromCart, updateCartQuantity } from 'shops-query/src/modules/cart/index.js';
import '../styles/ViewCart.css';

// Loading Skeleton for cart table rows
const CartRowSkeleton = () => (
  <tr className="cart-row-skeleton">
    <td className="product-column">
      <div className="product-info-skeleton">
        <div className="product-image-skeleton"></div>
        <div className="product-details-skeleton">
          <div className="product-name-skeleton"></div>
          <div className="product-price-skeleton"></div>
          <div className="product-title-skeleton"></div>
        </div>
      </div>
    </td>
    <td className="quantity-column">
      <div className="quantity-skeleton"></div>
    </td>
    <td className="total-column">
      <div className="total-skeleton"></div>
    </td>
  </tr>
);

// Free Shipping Progress Star Icon
const ShippingStarIcon = ({ filled }) => (
  <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path 
      d="M8 1L9.545 6.036H15L10.727 9.018L12.273 14.054L8 11.072L3.727 14.054L5.273 9.018L1 6.036H6.455L8 1Z" 
      fill={filled ? "#8B5A3C" : "none"} 
      stroke="#8B5A3C" 
      strokeWidth="1.5"
    />
  </svg>
);

// Payment Method Icons
const PaymentIcons = () => (
  <div className="payment-icons">
    <span className="payment-text">We accept</span>
    <div className="payment-methods">
      {/* Visa */}
      <div className="payment-icon visa">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#1A1F71"/>
          <text x="16" y="13" textAnchor="middle" fill="white" fontSize="8" fontWeight="bold">VISA</text>
        </svg>
      </div>
      
      {/* Mastercard */}
      <div className="payment-icon mastercard">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#EB001B"/>
          <circle cx="12" cy="10" r="6" fill="#FF5F00"/>
          <circle cx="20" cy="10" r="6" fill="#F79E1B"/>
        </svg>
      </div>
      
      {/* American Express */}
      <div className="payment-icon amex">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#006FCF"/>
          <text x="16" y="13" textAnchor="middle" fill="white" fontSize="6" fontWeight="bold">AMEX</text>
        </svg>
      </div>
      
      {/* Discover */}
      <div className="payment-icon discover">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#FF6000"/>
          <text x="16" y="13" textAnchor="middle" fill="white" fontSize="5" fontWeight="bold">DISCOVER</text>
        </svg>
      </div>
      
      {/* PayPal */}
      <div className="payment-icon paypal">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#003087"/>
          <text x="16" y="9" textAnchor="middle" fill="#009CDE" fontSize="6" fontWeight="bold">Pay</text>
          <text x="16" y="15" textAnchor="middle" fill="#009CDE" fontSize="6" fontWeight="bold">Pal</text>
        </svg>
      </div>
      
      {/* JCB */}
      <div className="payment-icon jcb">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#006CAB"/>
          <text x="16" y="13" textAnchor="middle" fill="white" fontSize="8" fontWeight="bold">JCB</text>
        </svg>
      </div>
      
      {/* Shop Pay */}
      <div className="payment-icon shoppay">
        <svg width="32" height="20" viewBox="0 0 32 20" fill="none">
          <rect width="32" height="20" rx="3" fill="#5A31F4"/>
          <text x="16" y="13" textAnchor="middle" fill="white" fontSize="5" fontWeight="bold">Shop</text>
        </svg>
      </div>
    </div>
  </div>
);

const ViewCart = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [orderNote, setOrderNote] = useState('');
  const [couponCode, setCouponCode] = useState('');
  const [selectedCountry, setSelectedCountry] = useState('');
  const [postalCode, setPostalCode] = useState('');
  
  // Constants
  const shopId = 512;
  const userId = 198;
  const imagePrefix = "https://s3.ap-south-1.amazonaws.com/business.strackit.com/";
  const freeShippingThreshold = 530;

  // Calculate cart totals
  const subtotal = cartItems.reduce((total, item) => {
    const price = parseFloat(item.prize || 0);
    const discount = parseFloat(item.Discount || 0);
    const finalPrice = price - discount;
    return total + (finalPrice * item.quantity);
  }, 0);

  const amountForFreeShipping = Math.max(0, freeShippingThreshold - subtotal);
  const freeShippingProgress = Math.min(100, (subtotal / freeShippingThreshold) * 100);

  // Fetch cart data
  const fetchCartData = async () => {
    try {
      setLoading(true);
      setError(null);
      const cartData = await fetchCart(shopId, userId);
      setCartItems(cartData || []);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching cart:', err);
    } finally {
      setLoading(false);
    }
  };

  // Update item quantity
  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;
    
    try {
      await updateCartQuantity({
        userId,
        productId,
        shopId,
        quantity: newQuantity
      });
      
      // Update local state
      setCartItems(prevItems =>
        prevItems.map(item =>
          item.productId === productId
            ? { ...item, quantity: newQuantity }
            : item
        )
      );
    } catch (err) {
      console.error('Error updating quantity:', err);
    }
  };

  // Remove item from cart
  const removeItem = async (productId) => {
    try {
      await removeFromCart({
        userId,
        productId,
        shopId
      });
      
      // Update local state
      setCartItems(prevItems =>
        prevItems.filter(item => item.productId !== productId)
      );
    } catch (err) {
      console.error('Error removing item:', err);
    }
  };

  // Handle navigation
  const handleContinueShopping = () => {
    navigate('/');
  };

  const handleCheckout = () => {
    // Implement checkout navigation
    console.log('Proceeding to checkout...');
  };

  // Handle image error
  const handleImageError = (e) => {
    e.target.src = 'data:image/svg+xml;base64,';
  };

  // Fetch data on component mount
  useEffect(() => {
    fetchCartData();
  }, []);

  return (
    <div className="view-cart-page">
      <div className="view-cart-container">
        {/* Page Title */}
        <h1 className="cart-page-title">Your Shopping Cart</h1>

        <div className="cart-layout">
          {/* Left Column - Cart Table */}
          <div className="cart-table-section">
            <table className="cart-table">
              <thead>
                <tr className="cart-header">
                  <th className="product-header">PRODUCT</th>
                  <th className="quantity-header">QUANTITY</th>
                  <th className="total-header">TOTAL</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <>
                    {[1, 2, 3].map(i => <CartRowSkeleton key={i} />)}
                  </>
                ) : error ? (
                  <tr>
                    <td colSpan="3" className="error-row">
                      <div className="error-message">
                        Error loading cart: {error}
                        <button onClick={fetchCartData} className="retry-button">Retry</button>
                      </div>
                    </td>
                  </tr>
                ) : cartItems.length === 0 ? (
                  <tr>
                    <td colSpan="3" className="empty-cart-row">
                      <div className="empty-cart-message">
                        Your cart is empty
                      </div>
                    </td>
                  </tr>
                ) : (
                  cartItems.map((item) => {
                    const price = parseFloat(item.prize || 0);
                    const discount = parseFloat(item.Discount || 0);
                    const finalPrice = price - discount;
                    const itemTotal = finalPrice * item.quantity;

                    return (
                      <tr key={`${item.productId}-${item.id}`} className="cart-row">
                        <td className="product-column">
                          <div className="product-info">
                            <div className="product-image">
                              <img 
                                src={item.featureImage ? `${imagePrefix}${item.featureImage}` : ''} 
                                alt={item.name}
                                onError={handleImageError}
                              />
                            </div>
                            <div className="product-details">
                              <h3 className="product-name">{item.name}</h3>
                              <div className="product-price">
                                {discount > 0 ? (
                                  <>
                                    <span className="discounted-price">${finalPrice.toFixed(2)}</span>
                                    <span className="original-price">${price.toFixed(2)}</span>
                                  </>
                                ) : (
                                  <span className="current-price">${price.toFixed(2)}</span>
                                )}
                              </div>
                              <div className="product-title">Title: <span>Default Title</span></div>
                              <button 
                                className="delete-link"
                                onClick={() => removeItem(item.productId)}
                              >
                                DELETE
                              </button>
                            </div>
                          </div>
                        </td>
                        <td className="quantity-column">
                          <div className="quantity-controls">
                            <button 
                              className="quantity-btn minus"
                              onClick={() => updateQuantity(item.productId, item.quantity - 1)}
                              disabled={item.quantity <= 1}
                            >
                              <FiMinus size={14} />
                            </button>
                            <span className="quantity-value">{item.quantity}</span>
                            <button 
                              className="quantity-btn plus"
                              onClick={() => updateQuantity(item.productId, item.quantity + 1)}
                            >
                              <FiPlus size={14} />
                            </button>
                          </div>
                        </td>
                        <td className="total-column">
                          <span className="item-total">${itemTotal.toFixed(2)}</span>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>

            {/* Continue Shopping Link */}
            <div className="continue-shopping-section">
              <button className="continue-shopping-link" onClick={handleContinueShopping}>
                CONTINUE SHOPPING
              </button>
            </div>
          </div>

          {/* Right Column - Order Summary */}
          <div className="order-summary-section">
            <div className="order-summary-box">
              {/* Free Shipping Progress */}
              {subtotal > 0 && (
                <div className="shipping-progress">
                  <div className="progress-info">
                    <ShippingStarIcon filled={freeShippingProgress >= 100} />
                    {amountForFreeShipping > 0 ? (
                      <span className="progress-text">
                        Buy <strong>${amountForFreeShipping.toFixed(2)} USD</strong> more to enjoy <strong>FREE shipping</strong>
                      </span>
                    ) : (
                      <span className="progress-text">
                         You qualify for <strong>FREE shipping</strong>
                      </span>
                    )}
                  </div>
                  <div className="progress-bar">
                    <div 
                      className="progress-fill" 
                      style={{ width: `${freeShippingProgress}%` }}
                    ></div>
                  </div>
                </div>
              )}

              {/* Order Special Instructions */}
              <div className="order-instructions">
                <label htmlFor="orderNote" className="section-label">Order special instructions</label>
                <textarea
                  id="orderNote"
                  className="order-note-textarea"
                  value={orderNote}
                  onChange={(e) => setOrderNote(e.target.value)}
                  placeholder=""
                  rows={4}
                />
              </div>

              {/* Coupon Section */}
              <div className="coupon-section">
                <label htmlFor="couponCode" className="section-label">Coupon</label>
                <input
                  id="couponCode"
                  type="text"
                  className="coupon-input"
                  value={couponCode}
                  onChange={(e) => setCouponCode(e.target.value)}
                  placeholder=""
                />
              </div>

              {/* Shipping Section */}
              <div className="shipping-section">
                <label className="section-label">Shipping</label>
                <div className="shipping-fields">
                  <div className="country-field">
                    <label htmlFor="country" className="field-label">Country/region</label>
                    <div className="select-wrapper">
                      <select
                        id="country"
                        className="country-select"
                        value={selectedCountry}
                        onChange={(e) => setSelectedCountry(e.target.value)}
                      >
                        <option value="">---</option>
                        <option value="US">United States</option>
                        <option value="CA">Canada</option>
                        <option value="GB">United Kingdom</option>
                        <option value="DE">Germany</option>
                        <option value="FR">France</option>
                        <option value="IN">India</option>
                        <option value="AU">Australia</option>
                      </select>
                      <FiChevronDown className="select-arrow" />
                    </div>
                  </div>
                  <div className="postal-field">
                    <label htmlFor="postalCode" className="field-label">Postal code</label>
                    <input
                      id="postalCode"
                      type="text"
                      className="postal-input"
                      value={postalCode}
                      onChange={(e) => setPostalCode(e.target.value)}
                      placeholder=""
                    />
                  </div>
                </div>
              </div>

              {/* Checkout Summary */}
              <div className="checkout-summary">
                <div className="tax-shipping-note">
                  Taxes and <span className="shipping-link">shipping</span> calculated at checkout
                </div>
                
                <button className="checkout-button" onClick={handleCheckout}>
                  CHECK OUT
                </button>
                
                <PaymentIcons />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewCart;
